INSERT INTO public.comment VALUES (4216, '2020-06-24 01:54:58', NULL, 'Il recommença, et Homais retira son bonnet de coton. De grosses larmes qui coulaient: -- Rodolphe! Rodolphe!... Ah! Rodolphe, cher petit Rodolphe! Minuit sonna. -- Minuit! dit-elle. Allons, c''est demain! encore un bal à la Corbeille.', true, 19, 1309);
