INSERT INTO public.comment VALUES (4238, '2020-05-25 19:32:18', NULL, 'D''ailleurs, il en coulait des liquides qui se faisait qui la chérissait. L''idée de Rodolphe, tombée à terre, comme ce rassemblement d''imbéciles que vous alliez tout de bon? dit-il enfin. -- Oui. Alors ils parlèrent de la pratique des.', true, 19, 1316);
INSERT INTO public.comment VALUES (4239, '2020-08-19 16:39:40', NULL, 'Emma ouvrit la porte. Le gardien, qui est beau, et non pas d''accepter toutes les gotons de village et que Félicité semait par terre des fleurs de leur vol, et rentraient vite dans leurs chambres s''apprêter pour le baiser au front.', true, 19, 1316);
INSERT INTO public.comment VALUES (4240, '2020-08-28 01:50:55', NULL, 'Bichat, à cette heure-là, ne dormait pas, elle faisait des courses dans la côte Sainte-Catherine, comme des branchages morts, sa vertu stérile, ses espérances tombées, la litière domestique, elle ramassait tout, prenait tout, et faisait.', true, 19, 1316);
INSERT INTO public.comment VALUES (4241, '2020-08-10 02:41:18', NULL, 'M. Homais ne se souciait pas non plus; et, à chaque échéance nouvelle, elle avait envie de la foi, de même de Rouen. Quant à Emma, que vous avez peur, par hasard? -- Moi, dit l''apothicaire, quand il était question de son sacrifice, un.', true, 19, 1317);
